userDefinedLinkHintCss:
div > .vimiumHintMarker {
  padding: 2px 3px;
  background-color: #feda31;
  border: 0;
  border-radius: 2px;
  box-shadow: inset 0 -2px 0 #b39922;
  background-image: none;
}

div > .vimiumHintMarker span {
  font-size: 15px;
  font-weight: bold;
  text-shadow: none;
  color: #4a400e;
}

div > .vimiumHintMarker > .matchingCharacter {
  color: #dcbc2a;
}
